mapper.update:
UPDATE club
club_title = #{clubTitle} , club_detail = #{clubDetail} , club_category = #{clubCategory} , club_area = #{clubArea} , club_image = #{clubImage}
WHERE club_no = #{clubNo}

mapper.delete:
UPDATE CLUB
SET DELETE_CONDITION = 1 
WHERE club_no = #{ clubNo }

mapper.select:
  	SELECT V3.*
	FROM ( SELECT V2.*
			FROM ( SELECT ROW_NUMBER() OVER( ORDER BY club_no DESC ) AS row_seq, V1.*
  					FROM		(	SELECT club_no , user_id , club_title , club_detail , club_reg_date, current_member, club_max_member, club_category, club_area, club_image
										FROM club
										WHERE delete_condition = 0
										  
												
													
														
														and club_title LIKE '%'||#{searchKeyword}||'%' and club_category = #{clubCategory} and club_area = #{clubArea}
																										
													
														
															
																
																and club_category = #{clubCategory}
															
															
																
																and club_area = #{clubArea}	
															
															
																
																and club_category = #{clubCategory} and club_area = #{clubArea}
															
														
																																							
													
														
															
																
																and club_title LIKE '%'||#{searchKeyword}||'%'
															
															
																
																and club_area = #{clubArea} and club_title LIKE '%'||#{searchKeyword}||'%'
															
																													
																										
													
														
														and club_category = #{clubCategory} and club_title LIKE '%'||#{searchKeyword}||'%'
													
																										
										  
										ORDER BY club_no ) V1
					 		) V2
					 ) V3
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT club_no , club_title , club_detail
					FROM club
					
						
							
					 			club_title = #{searchKeyword}
							
							
					 			club_category = #{searchKeyword}
							
						
					 ) countTable

SELECT inner_table.* FROM ( SELECT rownum AS row_seq, deep_table.* FROM ( SELECT c.club_no , c.club_title , c.club_detail , c.club_category , c.current_member , c.club_max_member , c.club_area , c.club_image , cU.approval_condition
FROM club c, club_User cU
c.club_no = cU.club_no
and cU.user_id = #{searchKeyword}
and cU.user_id = #{userId}
) deep_table ) inner_table

  	SELECT count(*)
  	FROM ( SELECT user_id
			FROM club_user		
		
			user_id = #{searchKeyword}
					
		) countTable

  	SELECT COUNT(*)
  	FROM(	SELECT club_no 
					FROM club_user
						
					 			club_no = #{ searchKeyword }
					 			and approval_condition = '1'								
						 ) countTable

mapper.update:
UPDATE meeting
meeting_title = #{meetingTitle} , meeting_content = #{meetingContent} , meeting_date = #{meetingDate} , meeting_place = #{meetingPlace} , meeting_time = #{meetingTime} , meeting_maximum_member = #{meetingMaximumMember}
WHERE meeting_no = #{meetingNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT meeting_no 
					FROM meeting
					
						
							
					 			club_no = #{searchKeyword}
															
						
					 ) countTable

mapper.insert:
INSERT into participant		(
	participant_no ,
	meeting_no ,
	participant_user_id		
	)		
VALUES (
	seq_participant_no.nextval ,
	#{meetingNo} ,
	#{user.userId}
)

mapper.delete:
DELETE
FROM participant
WHERE meeting_no = #{meetingNo} AND participant_user_id = #{user.userId}

mapper.select:
SELECT inner_table.* FROM ( SELECT rownum AS row_seq, deep_table.* FROM ( SELECT p.participant_no, p.participant_user_id, u.nickname , u.profile_image , m.meeting_title , m.meeting_place , m.meeting_time , m.meeting_date , m.club_no , m.meeting_no
FROM participant p, users u, meeting m
p.participant_user_id = u.user_id and p.meeting_no = m.meeting_no
and p.meeting_no = #{searchKeyword}
and u.user_id = #{searchKeyword}
) deep_table ) inner_table

  	SELECT COUNT(*)
  	FROM(	SELECT participant_no 
					FROM participant
					 
						
							meeting_no = #{searchKeyword}
						
						
						
							participant_user_id = #{searchKeyword}
						
					
		) countTable

