mapper.sql:
SELECT C.CLUB_POST_COMMENT_NO, C.CLUB_POST_NO, C.USER_ID, U.NICKNAME, U.PROFILE_IMAGE, C.COMMENT_CONTENT, C.COMMENT_REG_DATE, C.COMMENT_UPDATE_DATE, C.COMMENT_HEART_COUNT, C.COMMENT_COUNT, C.REPORT_CONDITION, C.DELETE_CONDITION, C.PARENT, C.DEPTH, C.SEQUENCE FROM CLUB_POST_COMMENT C, USERS U WHERE C.USER_ID = U.USER_ID AND C.CLUB_POST_NO = #{ comment.clubPostNo } AND C.DELETE_CONDITION = 0
AND C.PARENT = #{ comment.clubPostCommentNo }
ORDER BY C.CLUB_POST_COMMENT_NO ASC

mapper.select:
SELECT C.CLUB_POST_COMMENT_NO, C.CLUB_POST_NO, C.USER_ID, U.NICKNAME, U.PROFILE_IMAGE, C.COMMENT_CONTENT, C.COMMENT_REG_DATE, C.COMMENT_UPDATE_DATE, C.COMMENT_HEART_COUNT, C.COMMENT_COUNT,
C.REPORT_CONDITION, C.DELETE_CONDITION, C.PARENT, C.DEPTH, C.SEQUENCE
FROM CLUB_POST_COMMENT C, USERS U
WHERE C.USER_ID = U.USER_ID AND C.DELETE_CONDITION = 0

	AND C.CLUB_POST_COMMENT_NO = #{ clubPostCommentNo }
	AND C.CLUB_POST_NO = #{ clubPostNo } AND C.PARENT = #{ clubPostNo }

mapper.update:
UPDATE CLUB_POST_COMMENT SET
COMMENT_HEART_COUNT = COMMENT_HEART_COUNT + 1 COMMENT_HEART_COUNT = COMMENT_HEART_COUNT - 1 REPORT_CONDITION = 1 COMMENT_CONTENT = #{ commentContent }, COMMENT_UPDATE_DATE = SYSDATE
WHERE CLUB_POST_COMMENT_NO = #{ clubPostCommentNo }
