mapper.sql:
SELECT ROW_NUMBER() OVER(ORDER BY
CLUB_POST_HEART_COUNT DESC, CLUB_POST_NO DESC CLUB_POST_NO ASC
CLUB_POST_NO DESC
) AS R, V2.* FROM ( SELECT CP.CLUB_NO, CP.CLUB_POST_NO, CP.IMAGE1, CP.CLUB_POST_VIDEO1, CP.CLUB_POST_TITLE, CP.USER_ID, U.PROFILE_IMAGE, U.NICKNAME, CP.CLUB_POST_HEART_COUNT, HEART_CONDITION FROM CLUB_POST CP, USERS U, ( SELECT SOURCE_NO AS HEART_CONDITION FROM HEART WHERE TRIM(SOURCE) = '2' AND USER_ID = #{ clubPost.user.userId } ) V1 WHERE HEART_CONDITION (+) = CP.CLUB_POST_NO AND CP.USER_ID = U.USER_ID AND CLUB_NO =
#{ clubPost.clubNo } AND CP.USER_ID = #{ clubPost.user.userId }
AND REPORT_CONDITION = '0' AND DELETE_CONDITION = '0'
AND CP.USER_ID LIKE '%'||#{search.searchKeyword}||'%' AND CLUB_POST_CONTENT LIKE '%'||#{search.searchKeyword}||'%'
) V2

mapper.select:
