mapper.update:
UPDATE CLUB_POST SET
CLUB_POST_HEART_COUNT = CLUB_POST_HEART_COUNT + 1 CLUB_POST_HEART_COUNT = CLUB_POST_HEART_COUNT - 1 CLUB_POST_COMMENT_COUNT= CLUB_POST_COMMENT_COUNT + 1 CLUB_POST_COMMENT_COUNT= CLUB_POST_COMMENT_COUNT - 1 REPORT_CONDITION = 1
CLUB_POST_TITLE = #{ clubPost.clubPostTitle:VARCHAR }, CLUB_POST_CONTENT = #{ clubPost.clubPostContent:VARCHAR }, CLUB_POST_VIDEO1 = #{ clubPost.clubPostVideo1:VARCHAR }, CLUB_POST_VIDEO2 = #{ clubPost.clubPostVideo2:VARCHAR }, CLUB_POST_VIDEO3 = #{ clubPost.clubPostVideo3:VARCHAR }, CLUB_POST_UPDATE_DATE = sysdate, IMAGE1 = #{ clubPost.image1:VARCHAR }, IMAGE2 = #{ clubPost.image2:VARCHAR }, IMAGE3 = #{ clubPost.image3:VARCHAR }, IMAGE4 = #{ clubPost.image4:VARCHAR }, IMAGE5 = #{ clubPost.image5:VARCHAR }, IMAGE6 = #{ clubPost.image6:VARCHAR }, IMAGE7 = #{ clubPost.image7:VARCHAR }, IMAGE8 = #{ clubPost.image8:VARCHAR }, IMAGE9 = #{ clubPost.image9:VARCHAR }, IMAGE10 = #{ clubPost.image10:VARCHAR }
WHERE CLUB_POST_NO = #{ clubPost.clubPostNo }
