mapper.sql:
SELECT ROW_NUMBER() OVER(ORDER BY
CLUB_POST_HEART_COUNT DESC, CLUB_POST_NO DESC CLUB_POST_NO ASC
CLUB_POST_NO DESC
) AS R, V2.* FROM ( SELECT CP.CLUB_NO, CP.CLUB_POST_NO, CP.IMAGE1, CP.CLUB_POST_VIDEO1, CP.CLUB_POST_TITLE, CP.USER_ID, U.PROFILE_IMAGE, U.NICKNAME, CP.CLUB_POST_HEART_COUNT, HEART_CONDITION FROM CLUB_POST CP, USERS U, ( SELECT SOURCE_NO AS HEART_CONDITION FROM HEART WHERE TRIM(SOURCE) = '2' AND USER_ID = #{ clubPost.user.userId } ) V1 WHERE HEART_CONDITION (+) = CP.CLUB_POST_NO AND CP.USER_ID = U.USER_ID AND CLUB_NO =
#{ clubPost.clubNo } AND CP.USER_ID = #{ clubPost.user.userId }
AND REPORT_CONDITION = '0' AND DELETE_CONDITION = '0'
AND CP.USER_ID LIKE '%'||#{search.searchKeyword}||'%' AND CLUB_POST_CONTENT LIKE '%'||#{search.searchKeyword}||'%'
) V2

mapper.update:
UPDATE CLUB_POST SET
CLUB_POST_HEART_COUNT = CLUB_POST_HEART_COUNT + 1 CLUB_POST_HEART_COUNT = CLUB_POST_HEART_COUNT - 1 CLUB_POST_COMMENT_COUNT= CLUB_POST_COMMENT_COUNT + 1 CLUB_POST_COMMENT_COUNT= CLUB_POST_COMMENT_COUNT - 1 CLUB_POST_COMMENT_COUNT= CLUB_POST_COMMENT_COUNT - ${ comment.commentCount } REPORT_CONDITION = 1
CLUB_POST_TITLE = #{ clubPost.clubPostTitle }, CLUB_POST_CONTENT = #{ clubPost.clubPostContent }, CLUB_POST_VIDEO1 = #{ clubPost.clubPostVideo1:VARCHAR }, CLUB_POST_VIDEO2 = #{ clubPost.clubPostVideo2:VARCHAR }, CLUB_POST_VIDEO3 = #{ clubPost.clubPostVideo3:VARCHAR }, CLUB_POST_UPDATE_DATE = sysdate, IMAGE1 = #{ clubPost.image1:VARCHAR }, IMAGE2 = #{ clubPost.image2:VARCHAR }, IMAGE3 = #{ clubPost.image3:VARCHAR }, IMAGE4 = #{ clubPost.image4:VARCHAR }, IMAGE5 = #{ clubPost.image5:VARCHAR }, IMAGE6 = #{ clubPost.image6:VARCHAR }, IMAGE7 = #{ clubPost.image7:VARCHAR }, IMAGE8 = #{ clubPost.image8:VARCHAR }, IMAGE9 = #{ clubPost.image9:VARCHAR }, IMAGE10 = #{ clubPost.image10:VARCHAR }
WHERE CLUB_POST_NO = #{ clubPost.clubPostNo }

mapper.select:
SELECT *
FROM ( SELECT * FROM CHAT WHERE USER_ID = #{ user.userId } OR USER_ID2 = #{ user.userId } )
WHERE USER_ID = #{ user2.userId } OR USER_ID2 = #{ user2.userId }
