mapper.select:
SELECT f.feed_no, f.user_id, u.profile_image, u.nickName , f.feed_open_condition, f.feed_full_content, f.feed_content, f.feed_image1, f.feed_image2, f.feed_image3, f.feed_image4, f.feed_video, f.hashtag, f.feed_heart_count, f.feed_comment_count, f.feed_reg_date, f.feed_update_date, f.report_condition, f.delete_condition
, isLike.EXIST FROM feed f, users u, ( SELECT source_no as EXIST FROM HEART WHERE TRIM(source) = '0' AND user_id = #{user.userId} ) isLike WHERE f.feed_no = isLike.EXIST(+) AND f.user_id = u.user_id AND f.feed_no = #{feedNo}
FROM feed f, users u WHERE f.feed_no = #{feedNo} AND f.user_id = u.user_id

mapper.update:
UPDATE feed
feed_heart_count = feed_heart_count + 1 feed_heart_count = feed_heart_count - 1 feed_comment_count = feed_comment_count + 1 feed_comment_count = feed_comment_count - 1
feed_open_condition = #{openCondition:VARCHAR}, feed_full_content = #{fullContent:VARCHAR}, feed_content = #{content:VARCHAR}, feed_image1 = #{image1:VARCHAR}, feed_image2 = #{image2:VARCHAR}, feed_image3 = #{image3:VARCHAR}, feed_image4 = #{image4:VARCHAR}, feed_video = #{video:VARCHAR}, hashtag = #{hashtag:VARCHAR}, feed_update_date = SYSDATE, report_condition = #{reportCondition}
WHERE feed_no = #{feedNo}

UPDATE feed

	feed_comment_count				= feed_comment_count + 1

WHERE feed_no						= #{feedNo}

UPDATE feed_comment

	
		sequence	= TO_NUMBER(sequence) + 1
		sequence	= TO_NUMBER(sequence) - 1
	

WHERE sequence					>= #{sequence}
AND feed_no							= #{feedNo}

mapper.select:
SELECT
COUNT(*)
FROM feed_comment
WHERE feed_no			=	#{feedNo}

	
	AND parent			=	#{comment.parent}	
	
	
	AND parent			=	#{comment.feedCommentNo}

SELECT inner_table.* FROM ( SELECT rownum AS row_seq, deep_table.* FROM ( SELECT c.feed_comment_no, c.feed_no, c.user_id, u.nickName, u.profile_image, c.feed_comment_content, c.feed_comment_heart_count, c.feed_recomment_count, c.feed_comment_reg_date, c.feed_comment_update_date, c.report_condition, c.delete_condition, c.parent, c.depth, c.sequence
, isLike.EXIST FROM feed_comment c, users u, ( SELECT source_no as EXIST FROM HEART WHERE TRIM(source) = '1' AND user_id = #{user.userId}
) isLike
c.feed_comment_no = isLike.EXIST(+) AND c.user_id = u.user_id AND c.feed_no = #{feedNo} AND delete_condition = '0'
ORDER BY sequence ASC
FROM feed_comment c, users u WHERE delete_condition = '0' AND c.user_id = u.user_id AND c.feed_no = #{feedNo} ORDER BY sequence ASC
) deep_table
WHERE ROWNUM <= #{search.endRowNum} ) inner_table WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}
WHERE ROWNUM <= #{search.endRowNum} * #{search.order} ) inner_table WHERE row_seq BETWEEN 0 AND #{search.endRowNum} * #{search.order}
