mapper.select:
SELECT f.feed_no, f.user_id, f.feed_open_condition, f.feed_content, f.feed_image1, f.feed_image2, f.feed_image3, f.feed_image4, f.feed_video, f.hashtag, f.feed_heart_count, f.feed_comment_count, f.feed_reg_date, f.feed_update_date, f.report_condition, f.delete_condition
, isLike.EXIST FROM feed f, ( SELECT source_no as EXIST FROM HEART WHERE TRIM(source) = '0' AND user_id = #{user.userId} ) isLike WHERE f.feed_no = isLike.EXIST(+) AND f.feed_no = #{feedNo}
FROM feed f WHERE f.feed_no = #{feedNo}

mapper.update:
UPDATE feed
feed_heart_count = feed_heart_count + 1 feed_heart_count = feed_heart_count - 1 feed_comment_count = feed_comment_count + 1 feed_comment_count = feed_comment_count - 1
feed_open_condition = #{openCondition:VARCHAR}, feed_content = #{content:VARCHAR}, feed_image1 = #{image1:VARCHAR}, feed_image2 = #{image2:VARCHAR}, feed_image3 = #{image3:VARCHAR}, feed_image4 = #{image4:VARCHAR}, feed_video = #{video:VARCHAR}, hashtag = #{hashtag:VARCHAR}, feed_update_date = SYSDATE, report_condition = #{reportCondition}
WHERE feed_no = #{feedNo}

UPDATE feed

	feed_comment_count				= feed_comment_count + 1

WHERE feed_no						= #{feedNo}

UPDATE feed_comment
feed_comment_heart_count = feed_comment_heart_count + 1 feed_comment_heart_count = feed_comment_heart_count - 1
feed_comment_content = #{commentContent}, feed_comment_update_date = SYSDATE, report_condition = #{reportCondition}, parent = #{parent}, depth = #{depth}, sequence = #{sequence}
WHERE feed_comment_no = #{feedCommentNo}

UPDATE feed_comment

	sequence						= TO_NUMBER(sequence) + 1

WHERE sequence					>= #{sequence}
AND feed_no							= #{feedNo}
