mapper.select:
SELECT f.send_user_Id , f.recv_user_id, f.state, f.type, u.nickname , u.profile_image
FROM users u, follow_block f
u.user_id = f.recv_user_id
and f.send_user_id = #{userId} and f.recv_user_id =#{receiveId.userId} and TRIM(f.type) = #{fbType}
and f.send_user_id = #{userId} and f.recv_user_id =#{receiveId.userId} and TRIM(f.type) = #{fbType}
and f.send_user_id = #{userId} and f.recv_user_id =#{receiveId.userId}

SELECT inner_table.* FROM ( SELECT rownum AS row_seq , deep_table.* FROM ( SELECT f.send_user_Id , f.recv_user_id, f.state, f.type, u.nickname , u.profile_image
FROM users u, follow_block f
u.user_id = f.send_user_id
and f.recv_user_id = #{searchKeyword}
and f.recv_user_id = #{searchKeyword}
) deep_table
) inner_table
