mapper.update:
UPDATE notice

	notice_count = notice_count+1

WHERE notice_no = #{notice_no}

UPDATE NOTICE
NOTICE_TITLE = #{noticeTitle:VARCHAR} , NOTICE_CONTENT = #{noticeContent:VARCHAR}, NOTICE_REGDATE = SYSDATE , NOTICE_IMAGE1 = #{noticeImage1:VARCHAR}, NOTICE_IMAGE2 = #{noticeImage2:VARCHAR}
WHERE notice_no = #{noticeNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT NOTICE_TITLE , NOTICE_CONTENT , NOTICE_REGDATE
					FROM NOTICE
					
						
							
					 		AND	NOTICE_NO = #{searchKeyword}
							
							
					 		NOTICE_TITLE LIKE '%${searchKeyword}%'
							
						
					 ) countTable
