mapper.update:
UPDATE notice

	notice_count = notice_count+1

WHERE notice_no = #{notice_no}

UPDATE notice
notice_title = #{notice_title} , notice_content = #{notice_content}, notice_regdate = SYSDATE , notice_image1 = #{notice_image1}, notice_image2 = #{notice_image2}
WHERE notice_no = #{notice_no}

