mapper.update:
UPDATE QANDA
QANDA_TITLE = #{qandATitle}, QANDA_CONTENT= #{qandAContent},
QANDA_REGDATE = SYSDATE,
QANDA_IMAGE1 = #{qandAImage1:VARCHAR}, QANDA_IMAGE2 = #{qandAImage2:VARCHAR}, QANDA_OPEN_CONDITION= #{qandAOpenCondition:VARCHAR}
, ANSWER_CONTENT = #{qandAAnswerContent}, ANSWER_REGDATE = SYSDATE, ANSWER_CONDITION = '1'
WHERE QANDA_NO = #{qandANo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT QANDA_TITLE , QANDA_CONTENT , QANDA_REGDATE
					FROM QANDA
					
						
							
					 		QANDA_NO = #{searchKeyword}
							
							
					 		QANDA_TITLE LIKE '%${searchKeyword}%'
							
						
					 ) countTable

SELECT COUNT(*) FROM( SELECT Q.QANDA_TITLE , Q.QANDA_CONTENT , Q.QANDA_REGDATE FROM QANDA Q, USERS U WHERE Q.USER_ID = #{userId} AND Q.USER_ID = U.USER_ID
AND QANDA_NO = #{searchKeyword}
AND QANDA_TITLE LIKE '%${searchKeyword}%'
) countTable
